package ejercicio_2_3;

public class ejercicio_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Escribir un programa que imprima los números pares que hay entre los 20 primeros 
		// números enteros empezando por el cero. (Utilizando for y while)
		
		int i= 0;
		
		while( i <= 20) {
		      System.out.println(i);
		      i++; i++;
		    }
		
	}

}
